sys_variable_value.value:
(function execute(inputs, outputs) {

  /*
  This step builds the PDF from the source content and stores it with the document version record
  */
  var pageSize = new sn_pdfgeneratorutils.PdfPage("LETTER");
  var document = new sn_pdfgeneratorutils.Document.createDocument(pageSize);

  var docTitle = new sn_pdfgeneratorutils.Paragraph("Technical Design Document: " + inputs.document_title);
  document.addParagraph(docTitle);

  var secTitle1 = new sn_pdfgeneratorutils.Paragraph("Dictionary Entries");
  document.addParagraph(secTitle1);

  var wnutils = new WriteNowUtils();
  var sys_dictionary_tbl = wnutils.buildDictionary(inputs.table_name);
  document.addTable(sys_dictionary_tbl);
  document.saveAsAttachment('x_9736_writenow_technical_design_document_version', inputs.version_id.sys_id, inputs.document_title + '_' + inputs.version_id.number + '.pdf');

})(inputs, outputs);

